define('block_proeduca_myoverview/view_nav', 
    [
        'jquery',
        'core/custom_interaction_events',
        'block_proeduca_myoverview/repository',
        'block_proeduca_myoverview/view',
        'block_proeduca_myoverview/selectors'
    ],
    function(
        $,
        CustomEvents,
        Repository,
        View,
        Selectors
    ) {

        var SELECTORS = {
            FILTERS: '[data-region="filter"]',
            FILTER_OPTION: '[data-filter]',
            DISPLAY_OPTION: '[data-display-option]'
        };

        /**
         * Update the user preference for the block.
         *
         * @param {String} filter The type of filter: display/sort/grouping.
         * @param {String} value The current preferred value.
         */
        var updatePreferences = function(filter, value) {
            var type = null;
            if (filter == 'display') {
                type = 'block_myoverview_user_view_preference';
            } else if (filter == 'sort') {
                type = 'block_proeduca_myoverview_user_sort_preference';
            } else if (filter == 'customfieldvalue') {
                type = 'block_proeduca_myoverview_user_grouping_customfieldvalue_preference';
            } else {
                type = 'block_proeduca_myoverview_user_grouping_preference';
            }
            if (value == 'card'){
                var icondisplaycard = $('#buttondisplaycard');
                var icondisplaylist = $('#buttondisplaylist');
                icondisplaycard.addClass('active');
                icondisplaylist.removeClass('active');
            }
            if (value == 'list'){
                var icondisplaycard = $('#buttondisplaycard');
                var icondisplaylist = $('#buttondisplaylist');
                icondisplaylist.addClass('active');
                icondisplaycard.removeClass('active');
            }
            Repository.updateUserPreferences({
                preferences: [
                    {
                        type: type,
                        value: value
                    }
                ]
            });
        };

        /**
         * Event listener for the Display filter (cards, list).
         *
         * @param {object} root The root element for the overview block
         */
        var registerSelector = function(root) {

            var Selector = root.find(SELECTORS.FILTERS);

            CustomEvents.define(Selector, [CustomEvents.events.activate]);
            Selector.on(
                CustomEvents.events.activate,
                SELECTORS.FILTER_OPTION,
                function(e, data) {
                    var option = $(e.target);

                    if (option.hasClass('active')) {
                        // If it's already active then we don't need to do anything.
                        return;
                    }

                    if ( option[0].tagName == 'i' || option[0].tagName == 'I' ) {
                        option = option[0].parentNode;
                    }

                    var filter = option.attr('data-filter');
                    var pref = option.attr('data-pref');
                    var customfieldvalue = option.attr('data-customfieldvalue');

                    root.find(Selectors.courseView.region).attr('data-' + filter, option.attr('data-value'));
                    updatePreferences(filter, pref);

                    if (customfieldvalue) {
                        root.find(Selectors.courseView.region).attr('data-customfieldvalue', customfieldvalue);
                        updatePreferences('customfieldvalue', customfieldvalue);
                    }

                    // Reset the views.
                    View.init(root);

                    data.originalEvent.preventDefault();
                }
            );

            CustomEvents.define(Selector, [CustomEvents.events.activate]);
            Selector.on(
                CustomEvents.events.activate,
                SELECTORS.DISPLAY_OPTION,
                function(e, data) {
                    var option = $(e.target);

                    if (option.hasClass('active')) {
                        return;
                    }

                    if ( option[0].tagName == 'i' || option[0].tagName == 'I' ) {
                        option = option[0].parentNode;
                    }

                    var filter = $(option).data('display-option');
                    var pref = $(option).data('pref');

                    root.find(Selectors.courseView.region).attr('data-display', $(option).data('value'));
                    updatePreferences(filter, pref);
                    View.reset(root);
                    data.originalEvent.preventDefault();
                }
            );
        };

        /**
