    var hideFavouriteIcon = function(root, courseId) {
        var iconContainer = getFavouriteIconContainer(root, courseId);

        var isFavouriteIcon = iconContainer.find(SELECTORS.ICON_NOT_FAVOURITE);

        if (isFavouriteIcon) {
          // Si no es favorito, buscamos el elemento <i> y cambiamos su clase a iconic-star si no la tiene
          iconContainer.find('i.icon').removeClass('iconic-star-favorite').addClass('iconic-star');
        }
        else {
            // Si es favorito, buscamos el elemento <i> y cambiamos su clase a iconic-star-favorite si no la tiene
            iconContainer.find('i.icon').removeClass('iconic-star').addClass('iconic-star-favorite');
        }

    };

    /**
     * Show the favourite icon.
     *
     * @param  {Object} root The course overview container.
     * @param  {Number} courseId Course id number.
     */
    var showFavouriteIcon = function(root, courseId) {
        var iconContainer = getFavouriteIconContainer(root, courseId);

        var isFavouriteIcon = iconContainer.find(SELECTORS.ICON_IS_FAVOURITE);

        if (isFavouriteIcon) {
        // Si es favorito, buscamos el elemento <i> y cambiamos su clase a iconic-star-favorite si no la tiene
        iconContainer.find('i.icon').removeClass('iconic-star').addClass('iconic-star-favorite');
        }
        else {
          // Si no es favorito, buscamos el elemento <i> y cambiamos su clase a iconic-star si no la tiene
          iconContainer.find('i.icon').removeClass('iconic-star-favorite').addClass('iconic-star');
        }
    };

    /**
